đồ thị nhập khẩu;

draw((-8,0)--(8,0),Arrows(4));
draw((0,-8)--(0,8),Arrows(4));

f thực (x thực) {return 2-x;}
g thực(x thực) {return 2x-x^2;}

x thực;

draw(graph(f,-5,1),BeginArrow(4));
draw(graph(g,1,4),EndArrow(4));

ep thực = 0,2;

