for(real i=ceil(xmin/gx)*gx;i<=floor(xmax/gx)*gx;i+=gx) draw((i,ymin)--(i,ymax),gs); for(real i=ceil(ymin/gy)*gy;i<=floor(ymax/gy)*gy;i+=gy) draw((xmin,i)--(xmax,i),gs);

Nhãn lỏng lẻo; laxis.p=fontsize(10);

xaxis(" $x$ ",xmin,xmax,Ticks(laxis,Step=1.0,Size=2,NoZero),Arrows(6),above=true); yaxis(" $y$ ",ymin,ymax,Ticks(laxis,Step=1.0,Size=2,NoZero),Arrows(6),above=true); f1 thực(real x){return x/((x-1)*(x -2)*(x+2));} draw(graph(f1,-3.51,-2.01),linewidth(1.2)); draw(graph(f1,-1.99,0.99),linewidth(1.2)); (đồ thị (f1,1.01,1.99), độ rộng đường truyền (1.2)); vẽ (đồ thị (f1,2.01,4.61), độ rộng đường truyền (1.2));

